bool operator == (Point p, Point q)
{
  return (p.x == q.x && p.y == q.y && p.z == q.z);
}

Point operator + (Point p, Point q)
{
  Point r;
  r.x = p.x + q.x;
  r.y = p.y + q.y;
  r.z = p.z + q.z;
  return r;
}

Point operator - (Point p, Point q)
{
  Point r;
  r.x = p.x - q.x;
  r.y = p.y - q.y;
  r.z = p.z - q.z;
  return r;
}

Point operator - (Point p)
{
  Point r;
  r.x = -p.x;
  r.y = -p.y;
  r.z = -p.z;
  return r;
}

float
inner_product (Point p, Point q)
{
  return (p.x * q.x + p.y * q.y + p.z * q.z);
}

Point
outer_product (Point p, Point q)
{
  Point r;
  r.x = p.y * q.z - p.z * q.y;
  r.y = p.z * q.x - p.x * q.z;
  r.z = p.x * q.y - p.y * q.x;
  return r;
}

float
norm (Point p)
{
  return (std::sqrt (p.x * p.x + p.y * p.y + p.z * p.z));
}

float
sqrt (Point p)
{
  return (std::sqrt (norm (p)));
}

float
distance (Point p, Point q)
{
  return (norm (p - q));
}

void
normalize (Point &p)
{
  float n = sqrt (p);
  p.x /= n;
  p.y /= n;
  p.z /= n;
}

Point
normalize (Point p)
{
  Point r;
  float n = sqrt (p);
  r.x = p.x / n;
  r.y = p.y / n;
  r.z = p.z / n;
  return r;
}

