sin (Point p)
{
  Point r;
  float s = std::sin (p.z);
  r.x = std::sin (p.x) * s;
  r.y = std::sin (p.y) * s;
  r.z = std::cos (p.x) * s;
  return r;
}

Point
cos (Point p)
{
  Point r;
  float c = std::cos (p.z);
  r.x = std::cos (p.x) * c;
  r.y = std::cos (p.y) * c;
  r.z = std::sin (p.x) * c;
  return r;
}

float
dot_product (Point a, Point b)
{
  return (a.x * b.x + a.y * b.y + a.z * b.z);
}

Point
cross_product (Point a, Point b)
{
  Point r;
  r.x = a.y * b.z - a.z * b.y;
  r.y = a.z * b.x - a.x * b.z;
  r.z = a.x * b.y - a.y * b.x;
  return r;
}

void
left_multiply (Point p, float f)
{
  p.x *= f;
  p.y *= f;
  p.z *= f;
