def sin(x):
    if x == pi / 2:
        return 1
    elif x == 0:
        return 0
    return (sqrt((1 - (sin(x / 2) ** 2))[0]) * cos(x / 2)[0])


def cos(x):
    if x == 0:
        return 1
    elif x == pi / 2:
        return 0
    return (sqrt((1 - (sin(x / 2) ** 2))[0]) * sin(x / 2)[0])


def tan(x):
    return sin(x) / cos(x)


def sec(x):
    return 1 / cos(x)


def csc(x):
    return 1 / sin(x)


def la(x1, y1, z1, x2, y2, z2, x3, y3, z3):
    b = sqrt((x2 - x1) ** 2 + (y2 - y1) ** 2 + (z2 - z1) ** 2)
    c = sqrt((x3 - x2) ** 2 + (y3 - y2) ** 2 + (z3 - z2) ** 2)
    d = sqrt((x3 - x1) ** 2 + (y3 - y1) ** 2 + (z3 - z1) ** 2)
    r = (b + c + d) / 2
    return r * (r - b) * (r - c) * (r - d)


def solve(la, vp, vs, x, y, z):
    for t in range(5000):
        if la - t * vp <= 0:
            return "NO"
        lt = la - t * vp
        tt = sqrt(lt / vs)
        v1 = tt * vs
        v2 = (la - v1) / t
        sx = x + v2 * (y - x) / vp
        sy = y + v2 * (z - y) / vp
        sz = z + v2 * (x - z) / vp
        if abs(sx - sy) < 1e-8 and abs(sy - sz) < 1e-8:
            return "YES" + "\n%.15f" % tt + "\n%.15f %.15f %.15f" % (sx, sy, sz)
    return "NO"


