def solve():
    n,mx,reg = [int(i) for i in input().split()]
    damage = []
    time = []
    used = [0]*n
    ans = 0
    s = 0
    for i in range(n):
        p,dmg = [int(i) for i in input().split()]
        damage.append(dmg)
        time.append(p)
    p = []
    for i in range(n):
        if time[i] <= 100*reg:
            p.append((damage[i],i+1))
    p.sort(reverse = True)
    while True:
        if mx <= 0:
            print('YES')
            print(ans)
            print(len(used),'\n')
            for i in range(len(used)):
                if used[i]:
                    print(i,used[i],sep ='')
            return
        if len(p) == 0:
            if mx > 100*reg:
                mx -= reg
                ans += 1
            continue
        p_dam,p_ind = p.pop()
        if p_dam <= reg:
            if mx > 100*reg:
                mx -= reg
                ans += 1
            continue
        p_time = time[p_ind-1]
        if s + p_time <= mx:
            used[p_ind-1] = ans+1
            mx -= p_dam
            ans += 1
        else:
            reg_dam = min(reg,mx)
            mx -= reg_dam
            ans += reg_dam//reg
            p_dam -= reg_dam
            if p_dam <= reg:
                if mx > 100*reg:
                    mx -= reg
                    ans += 1
            else:
                p.append((p_dam,p_ind))
        s += 1
if __name__ == '__main__':
    solve()


