def iter_solutions(n, max_iter):
    while n!= 1 and n!= 13 and n!= 12:
        if n % 12 == 0:
            n //= 12
        elif n % 13 == 0:
            n //= 13
        else:
            yield n
            break

        yield n

        max_iter -= 1
        if max_iter <= 0:
            break

    if n == 1 or n == 13 or n == 12:
        yield n

def find_ways(a):
    ways = []

    for i in range(120):
        di = 12 * 13 ** i
        if di == a:
            ways.append(a)
        if di > a:
            break

    if a in ways:
        ways.remove(a)
    return [*filter(lambda x: a // x > 0, ways)]

def powmod(a, p, m):
    r = 1
    while p:
        if p & 1:
            r = r * a % m
        a = a * a % m
        p = p // 2
    return r

def mod_inverse(a, m):
    return powmod(a, m - 2, m)

