def generate_subsets(n, length):
    return list(itertools.combinations(range(n), length))


def calc_ones(s):
    res = 0
    for ch in s:
        if ch == '1':
            res += 1
    return res


def calc_solution_ones(s):
    res = 0
    for idx, ch in enumerate(s):
        if ch == '1':
            res += (2**idx)
    return res


def calc_mask(bit_idx, length):
    mask = 0
    for i in range(length):
        mask += (2**bit_idx)
        bit_idx += 1
    return mask


def calc_mask_bit(mask, length):
    for bit in range(length):
        if mask & (1 << bit):
            return bit
    return -1


def generate_binary(n):
    return ''.join(['1' if num == 1 else '0' for num in range(n)])


def generate_binary_code(solution, max_length):
    for code_length in range(1, max_length + 1):
        for subset in generate_subsets(solution, code_length):
            for i in range(2**code_length):
                code = generate_binary(code_length)
                for idx in subset:
                    code = code[:idx] + str(int(solution[idx])^int(code[idx])) + code[idx + 1:]
                yield code


def calc_solutions(solution, correct, max_length):
    if correct == 0:
        return {solution}
    return {
        key: correct - calc_solutions(key, correct - 1, max_length).get(key, 0)
        for key in generate_binary_code(solution, max_length)
    }


def is_valid(attempt, correct, max_length):
    solutions = calc_solutions(attempt, correct, max_length)
    return any(sol in solutions.keys() for sol in solutions)


