from collections import defaultdict
from heapq import heappush, heappop


def dijkstra(n, graph, s, g):
    inf = float('inf')
    visited = [False]*n
    distances = [inf]*n
    prev = [None]*n
    hq = [(0, s)]

    while hq:
        dist_u, u = heappop(hq)
        if visited[u]:
            continue
        visited[u] = True
        if u == g:
            break
        for v, dist_v in graph[u]:
            if not visited[v]:
                new_dist = dist_u + dist_v
                if distances[v] > new_dist:
                    heappush(hq, (new_dist, v))
                    distances[v] = new_dist
                    prev[v] = u

    path = []
    if u == g:
        while u is not None:
            path.append(u)
            u = prev[u]
        return path[::-1]


def solve(n, m, k, grid):
    graph = defaultdict(list)
    s = None
    t = None
    for i in range(n):
        for j in range(m):
            if grid[i][j] in "ST":
                label = grid[i][j]
            elif len(graph[i+1, j]) < k and grid[i][j]!= grid[i+1][j]:
                graph[i+1, j].append((i, 1))
                graph[i, j].append((i+1, -1))
            if grid[i][j]!= grid[i][j+1] and (len(graph[i+1, j+1]) < k or (grid[i][j]!= grid[i+1][j+1] and (label is not None and grid[i][j]!= label))):
                graph[i+1, j+1].append((i, 1))
                graph[i, j].append((i+1, -1))
            if grid[i][j]!= grid[i-1][j] and (len(graph[i, j+1]) < k or (grid[i][j]!= grid[i-1][j] and (label is not None and grid[i][j]!= label))):
                graph[i, j+1].append((i-1, -1))
                graph[i-1, j].append((i, 1))

    for i in range(n):
        for j in range(m):
            if grid[i][j] == "S":
                s = i, j
            elif grid[i][j] == "T":
                t = i, j
    path = dijkstra(n*m, graph, s, t)
    if path is None:
        return -1
    ans = [grid[x][y] for x, y in path]
    print("".join(ans))


