def main():
    import sys
    input = sys.stdin.readline

    N = int(input())
    A = list(map(int, input().split()))

    stack = []
    ans = 0
    for i in range(N):
        while stack and stack[-1][0] <= A[i]:
            _, id, turn = stack.pop()
            if turn:
                ans += id
                if stack:
                    stack[-1][0] = A[id]
            else:
                break

        if not stack:
            ans += N-1
        else:
            ans += stack[-1][1]
        stack.append((A[i], i, (i+1)%N > stack[-1][2]))
        if i == N-1:
            stack = [(A[i], i, False)]
            ans += stack[-1][1]
    print(ans)


if __name__ == '__main__':
    main()
