h = int(input())
t = input()

time = t.split(":")

def correct(hh,mm):
	if(h == 12):
		if(hh < 1 or hh > 12 or mm < 0 or mm > 59):
			return(1)
	else:
		if(hh < 0 or hh > 23 or mm < 0 or mm > 59):
			return(1)
	return(0)

def count_diff(hh,mm):
	if(h == 12):
		h = hh
	else:
		h = (hh + 24)%24

	diff = 0
	i = 0
	while(i < 2):
		if(i == 0):
			temp = h
			h = int(time[i])
		else:
			temp = int(time[i])
			i+=1
		if(temp > h):
			diff += temp-h
		else:
			diff += h-temp

	while(i < 3):
		if(i == 2):
			temp = mm
		else:
			temp = int(time[i])
			i+=1
		if(temp > mm):
			diff += temp-mm
		else:
			diff += mm-temp

	return(diff)


def check(hh,mm,i):
	if(i == 2):
		mm = int(time[2])
		temp = int(time[i])
	else:
		mm = int(time[2])
		hh = int(time[0])
		temp = int(time[i])

	diff1 = count_diff(hh,mm)
	diff2 = count_diff(hh,temp)
	diff3 = count_diff(temp,mm)

	if(diff1 <= diff2 and diff1 <= diff3):
		h = temp
		i+=1
		return(check(h,mm,i))

	if(diff2 <= diff1 and diff2 <= diff3):
		mm = temp
		i+=1
		return(check(hh,mm,i))

	if(diff3 <= diff1 and diff3 <= diff2):
		hh = temp
		i+=1
		return(check(hh,mm,i))


