def find_powers_of_2(num):
    powers = 0
    while num > 0:
        powers += 1
        num -= num//2
    return powers

def find_invariant_pos(name):
    powers_a = find_powers_of_2(name[:m//2])
    powers_b = find_powers_of_2(name[m-1:m//2:-1])
    if powers_a == powers_b:
        return powers_a
    return -1

ans = 1
for i in range(n):
    inv_a = find_invariant_pos(name_map[name_map[i]][0])
    if inv_a == -1:
        continue
    for j in range(i + 1, n):
        inv_b = find_invariant_pos(name_map[name_map[i]][j])
        if inv_b == -1:
            continue
        if inv_b < inv_a:
            continue
        ans *= len(name_map[name_map[i]][j])
        ans %= mod

print(ans)

