def solve(n, m, mp):
    ans = []
    for i in range(n):
        x = [j for j, k in enumerate(mp[i]) if len(k) >= 2]
        ans.extend([k.pop() for k in mp[i] if len(k) >= 2])
    for j in range(m):
        if len([j for i, j in mp if j in mp[i][j]]) >= 2:
            ans.extend(
                sorted([j for i, j in mp if j in mp[i][j]], reverse=True)
            )
    print("".join(ans))


