def crossword(rows):
    # print(rows)
    for i in range(len(rows)):
        letterCount = {}
        for j in rows[i]:
            if j in letterCount:
                letterCount[j] += 1
            else:
                letterCount[j] = 1
        # print(letterCount)
        for key, value in letterCount.items():
            if value > 1:
                letterCount[key] = 0
        # print(letterCount)
        newLine = ''
        for j in rows[i]:
            if letterCount[j] > 0:
                newLine += j
        rows[i] = newLine
    # print(rows)
    return rows


