	if x==1:
		if n>=y-1:
			return 1
		return -1
	if y<=x+1:
		return -1
	elif 4*(x+1)<=(n*(n+2)-y):
		return -1
	if x*x<n*(n+1)-2*y:
		return -1
	elif x*x-(n-1)*y>n-1:
		return -1
	else:
		return 1

