from collections import defaultdict

class Graph:
    """
    Represents a directed graph with no isolated vertices.
    """
    def __init__(self, n=None):
        if n is None:
            n = []
        self.nodes = set(n)

    def add(self, node):
        self.nodes.add(node)

    def adj_nodes(self, node):
        for adj_node in self.nodes:
            if adj_node!= node:
                yield adj_node

def main():
