import sys

class DualDict:
    def __init__(self):
        self.dic = {}
        self.rev = {}

    def add(self, first, second):
        self.dic[first] = second
        self.rev[second] = first

    def add_many(self, d):
        for k, v in d.items():
            self.add(k, v)

    def is_key(self, key):
        return key in self.dic

    def keys(self):
        return self.dic.keys()

    def values(self):
        return self.dic.values()

    def items(self):
        return self.dic.items()

    def get(self, first):
        if self.is_key(first):
            return self.dic[first]
        return None

    def reverse_get(self, second):
        if self.is_key(second):
            return self.rev[second]
        return None

    def remove(self, key):
        if key in self.dic:
            del self.dic[key]
            return True
        return False

    def remove_value(self, value):
        if value in self.rev:
            del self.rev[value]
            return True
        return False

    def swap(self, a, b):
        if a in self.dic and b in self.dic:
            temp = self.dic[b]
            self.dic[b] = self.dic[a]
            self.dic[a] = temp
            temp = self.rev[b]
            self.rev[b] = self.rev[a]
            self.rev[a] = temp
            return True
        return False

    def __len__(self):
        return len(self.dic)

    def __str__(self):
        s = ''
        for k in self.dic:
            s += str(k) +':'+ str(self.dic[k]) + '\n'
        return s



