# Calculate factorial
def fac(n, m):
    if (n == m or n == m + 1):
        return 1
    if (n < m):
        return 0
    return fac(n - 1, m) + fac(n - 1, m - 1)

# Calculate nCr
def nCr(n, r):
    return fac(n, r) // fac(r, 1) // fac(n - r, 1)

