# Main function to calculate diameter of a tree
def diameter(n, g):
    # print(n,g)
    D = [-1] * (n + 1)
    P = [i for i in range(n + 1)]
    DEP = [0] * (n + 1)
    order = []
    for i in range(n):
        for j in g[i]:
            DEP[i] = max(DEP[i], DEP[j] + 1)
    for i in range(n):
        order.append((DEP[i], i))
    order.sort()
    last = 0
    diameter = -1
    for d, v in order:
        if (last == 0):
            last = v
            diameter = DEP[v]
        elif (DEP[v] > diameter):
            last = v
            diameter = DEP[v]
    # print(D,P,DEP)
    return diameter

