def fun(arr, n):
    x = min(arr[0], arr[-1])
    y = min(arr[0], arr[-1])
    pos = 0
    for i in range(n):
        if arr[i] < x:
            pos = i
            x = arr[i]
    i = 0
    j = pos
    while j!= i:
        if j > i:
            if arr[j] - arr[i] < y:
                y = arr[j] - arr[i]
                ind1 = i
                ind2 = j
            j += 1
        else:
            if arr[n + j] - arr[i] < y:
                y = arr[n + j] - arr[i]
                ind1 = i
                ind2 = n + j
            j -= 1
    return (ind1 + 1, ind2 + 1)


