def gcd(x, y):
    """ greatest common divisor of x and y """
    while y:
        x, y = y, x % y
    return x


def lcm(a, b):
    """ lowest common multiple """
    return a * b // math.gcd(a, b)


def is_prime(x, max_num=10**5):
    """ check if x is a prime number """
    if x <= 1:
        return False
    if x in (2, 3, 5, 7):
        return True
    if x % 2 == 0:
        return False
    i = 3
    while x!= 1 and i <= math.sqrt(x):
        if x % i == 0:
            return False
        i += 2
    return True


