def solve(N, M, A):
    B = []
    d = 10**18
    for v, h in enumerate(A):
        if d > h:
            d = h
            B.append(v)
    B.append(N)

    d = A[-1]
    for v in reversed(range(N+1)):
        if d - 1 <= A[v] <= d + M:
            d = A[v]
            B.append(N-v)
        elif A[v] < d - M:
            break

    a, b = 0, len(B) - 1
    while b > a + 1:
        m = (a+b)//2
        if b == N:
            a = m
        elif a == 0:
            b = m
        else:
            if B[b] - B[a] >= b-a-2:
                a = m
            else:
                b = m

    res = [[B[i], B[i+1]] for i in range(a, b)]

    return len(res), res

def main():
    N, M = map(int, input().split())
    A = [int(x) for x in input().split()]
    print(*solve(N, M, A))

if __name__ == "__main__":
    main()

