def find_min_inversion(n,A,inv,prev,NEXT,LEFT,RIGHT):
    lm = 0
    rm = n - 1
    while lm < rm:
        mid = (lm + rm) >> 1
        if A[inv[mid]]!= A[mid]:
            rm = mid
        else:
            lm = mid + 1
    left = mid - 1
    right = mid
    while left >= lm:
        A[inv[left]], A[left] = A[left], A[inv[left]]
        prev[inv[left]] = LEFT if A[inv[left]]!= A[left] else RIGHT
        left -= 1
    while right <= rm:
        A[inv[right]], A[right] = A[right], A[inv[right]]
        prev[inv[right]] = LEFT if A[inv[right]]!= A[right] else RIGHT
        right += 1
    mid = lm + 1
    for i in range(lm + 1, rm + 1):
        if A[inv[i]] > A[i]:
            mid = i
            break
    return mid


def solve(n,A):
    inv = [i for i in range(n)]
    random.shuffle(inv)
    prev = [0 for i in range(n)]
    NEXT = [0 for i in range(n)]
    LEFT = -1
    RIGHT = -1
    min_inv = find_min_inversion(n,A,inv,prev,NEXT,LEFT,RIGHT)
    if min_inv >= n - 1:
        return -1
    inv2 = [inv[i] for i in range(n)]
    random.shuffle(inv2)
    inv = inv2
    inv_index = [i for i in range(n)]
    random.shuffle(inv_index)
    for i in range(n):
        inv2[inv_index[i]] = inv[i]
    inv = inv2
    count = 0
    for i in range(n):
        if prev[inv[i]] == 0:
            start = inv[i]
            end = find_right_bound(inv,start,NEXT,LEFT,RIGHT,count)
            count += end - start
    return count

def find_right_bound(inv,start,NEXT,LEFT,RIGHT,count):
    end = NEXT[inv[start]]
    if LEFT == -1 or RIGHT == -1:
        return end
    while end < n and inv[end] == inv[end - 1]:
        end = NEXT[inv[end]]
    while inv[end] == inv[end - 1]:
        end = RIGHT[inv[end]]
    return end


