class SegTree():
    def __init__(self, a, intv, init_val):
        n = 1 << (len(intv)-1).bit_length()
        self.n = n
        self.init_val = init_val
        self.intv = intv
        self.table = defaultdict(lambda:init_val)
        for i in range(n):
            if i+1 <= len(a):
                self.table[i+1] = intv(a[i])
            if i+n <= len(a):
                self.table[i+n] = self.operation(self.table[i+n-n], self.table[i+n])

    def __getitem__(self, idx):
        if idx <= 0 or idx > self.n:
            raise Exception
        return self.table[idx]

    def set_val(self, idx, val):
        if idx <= 0 or idx > self.n:
            raise Exception
        if type(val)!= int:
            val = self.intv(val)
        self.table[idx] = val
        idx += 1
        idx >>= 1
        while idx > 0:
            self.table[idx] = self.operation(self.table[2*idx], self.table[2*idx+1])
            idx >>= 1

    def build(self, a):
        self.n = len(a)
        self.table = defaultdict(lambda:self.init_val)
        for i in range(self.n):
            self.table[i+1] = self.intv(a[i])
        idx = 1
        while idx < self.n:
            self.table[idx] = self.operation(self.table[2*idx], self.table[2*idx+1])
            idx *= 2

    def fold(self, i, j):
        if i > j:
            return self.init_val
        if i == j:
            return self.table[i]
        else:
            return self.operation(self.table[i], self.table[j])

    def fold_range(self, i):
        if i <= 0 or i > self.n:
            raise Exception
        idx = i
        ret = []
        while idx > 0:
            ret.append(self.table[idx])
            idx >>= 1
        return self.operation(*ret)

    def operation(self, left, right):
        raise Exception

    def __str__(self):
        return str(self.table)

class RangeSumTree(SegTree):
    def __init__(self, a, intv=int):
        super().__init__(a, intv, 0)

    def operation(self, left, right):
        return left+right

