mod = 998244353

class ModPow:
    def __init__(self, base, pow):
        self.base = base
        self.pow = pow
        self.mod = mod

    def __pow__(self, other):
        if other == 1:
            return ModPow(self.base, self.pow)

        b = ModPow(self.base, self.pow)
        p = ModPow(self.base, self.pow // 2)
        p **= other // 2
        if other & 1:
            p *= b
        return p

    def __mul__(self, other):
        b = ModPow(self.base, self.pow + other.pow)
        return ModPow(b.base, b.pow)


