def solve(N, M, D, l):
    max_valid = l[D]
    if max_valid < N:
        return -1
    if max_valid == 0:
        return 0
    if max_valid == 1:
        return 1
    ans = 1
    for i in range(M):
        if i!= D:
            ans *= (l[i] + 1) / (max_valid - 1)
    return 1 - ans


