def main():
    n, m, k = map(int, input().split())
    graph = {}
    dist = {(i, j): float("inf") for i in range(n) for j in range(m)}
    sx, sy, tx, ty = None, None, None, None
    for i in range(n):
        row = input()
        for j, cell in enumerate(row):
            if cell.isalpha() and cell.islower():
                if graph.get(cell) is None:
                    graph[cell] = []
                graph[cell].append((i, j))
            elif cell == 'S':
                sx, sy = i, j
            elif cell == 'T':
                tx, ty = i, j
    assert sx is not None and sy is not None and tx is not None and ty is not None
    for start in graph.keys():
        dist[(sx, sy)] = 0
        bfs_queue = [(sx, sy)]
        bfs_set = [(sx, sy)]
        while len(bfs_queue) > 0:
            x, y = bfs_queue.pop(0)
            for i, j in graph.get(start, []) + graph.get(start.upper(), []):
                if (i, j) not in bfs_set:
                    bfs_queue.append((i, j))
                    bfs_set.append((i, j))
                    dist[(i, j)] = dist[(x, y)] + 1
        dist[(tx, ty)] = 0
        bfs_queue = [(tx, ty)]
        bfs_set = [(tx, ty)]
        while len(bfs_queue) > 0:
            x, y = bfs_queue.pop(0)
            for i, j in graph.get(start, []) + graph.get(start.upper(), []):
                if (i, j) not in bfs_set:
                    bfs_queue.append((i, j))
                    bfs_set.append((i, j))
                    dist[(i, j)] = min(dist[(i, j)], dist[(x, y)] + 1)
        if dist[(tx, ty)] <= k:
            break
    else:
        print(-1)
        exit(0)
    path = []
    for i, j in [(tx, ty), (sx, sy)]:
        i, j = sx, sy
        while True:
            if len(path) == 0 or start!= path[-1]:
                path.append(start)
            if dist[(i, j)] <= k and (i, j) == (tx, ty):
                break
            min_cost = float("inf")
            min_idx = -1
            for ii, jj in graph.get(start, []) + graph.get(start.upper(), []):
                if (ii, jj)!= (tx, ty):
                    if dist[(ii, jj)] + 1 < min_cost:
                        min_cost = dist[(ii, jj)] + 1
                        min_idx = (ii, jj)
            i, j = min_idx
            start = graph.keys()[graph.values().index(min_idx)]
    print("".join(path))


# region fastio
BUFSIZE = 8192


class FastIO(IOBase):
    newlines = 0

    def __init__(self, file):
        self._fd = file.fileno()
        self.buffer = BytesIO()
        self.writable = "x" in file.mode or "r" not in file.mode
        self.write = self.buffer.write if self.writable else None

    def read(self):
        while True:
            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))
            if not b:
                break
            ptr = self.buffer.tell()
            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)
        self.newlines = 0
        return self.buffer.read()

    def readline(self):
        while self.newlines == 0:
            b = os.read(self._fd, max(os.fstat(self._fd).st_size, BUFSIZE))
            self.newlines = b.count(b"\n") + (not b)
            ptr = self.buffer.tell()
            self.buffer.seek(0, 2), self.buffer.write(b), self.buffer.seek(ptr)
        self.newlines -= 1
        return self.buffer.readline()

    def flush(self):
        if self.writable:
            os.write(self._fd, self.buffer.getvalue())
            self.buffer.truncate(0), self.buffer.seek(0)


class IOWrapper(IOBase):
    def __init__(self, file):
        self.buffer = FastIO(file)
        self.flush = self.buffer.flush
        self.writable = self.buffer.writable
        self.write = lambda s: self.buffer.write(s.encode("ascii"))
        self.read = lambda: self.buffer.read().decode("ascii")
        self.readline = lambda: self.buffer.readline().decode("ascii")


sys.stdin, sys.stdout = IOWrapper(sys.stdin), IOWrapper(sys.stdout)
