#include <bits/stdc++.h>
using namespace std;
const int sz = 2e5 + 10;
string s;
int k;
int main() {
  cin >> s >> k;
  if (s.size() < k) {
    cout << "impossible\n";
    return 0;
  }
  int f = 0, cnt = 0;
  for (int i = 0; i < s.size(); i++) {
    if (!f &&!cnt) {
      f = 1;
      cnt++;
    } else if (!f) {
      if (s[i]!= s[i - 1]) {
        f = 1;
        cnt++;
      }
    } else {
      if (s[i]!= s[i - 1]) {
        cnt++;
      }
    }
  }
  cout << s.size() - cnt << endl;
}

