int main() {
  cin >> s;
  cin >> k;
  long long int i = 1, l = s.size(), r = 1e9 + 1000, cnt = 0, mid;
  while (i <= r) {
    mid = (i + r) / 2;
    if (ok(mid)) {
      r = mid - 1;
      cnt = mid;
    } else
      i = mid + 1;
  }
  if (cnt < k)
    cout << "impossible";
  else
    cout << (k - cnt);
  return 0;
}

