int n, t, cnt[1000100], ans, mx;
map<int, int> mp;
int main() {
  scanf("%d", &n);
  while (n--) {
    scanf(" %c%d", &t, &cnt[1]);
    if (t == '+')
      mp[cnt[1]]++;
    else
      mp[cnt[1]]--;
    int tmp = 0;
    for (int i = 1; i < 1000100; i++) tmp += (cnt[i]!= 0);
    if (tmp > mx) mx = tmp;
  }
  for (auto p : mp) {
    ans += p.second;
    if (p.second > 0) mx += 2 * p.second;
  }
  printf("%d", mx - ans);
}

