#include <bits/stdc++.h>
using namespace std;
int main() {
  ios::sync_with_stdio(false);
  int n, a, b;
  cin >> n >> a >> b;
  if (n > a * b) {
    cout << -1 << endl;
    return 0;
  }
  int x, y;
  x = (n + a - 1) / a;
  y = n - (x - 1) * a;
  if (y == 0) {
    y = a;
    x--;
  }
  vector<vector<int> > ans(a, vector<int>(b));
  for (int i = 0; i < a; i++) {
    for (int j = 0; j < b; j++) {
      int u = a * y;
      u += (i + j + 1) * (a - y);
      if (u > n) continue;
      ans[i][j] = u;
    }
  }
  if (n < a * b) {
    for (int i = 0; i < x; i++) {
      for (int j = 0; j < b; j++) {
        ans[i][j] = 0;
      }
    }
  }
  for (int i = 0; i < a; i++) {
    for (int j = 0; j < b; j++) {
      cout << ans[i][j] << " ";
    }
    cout << endl;
  }
}

