int n, a, b;
int s[200][200];
int main() {
  cin >> n >> a >> b;
  if (n > a * b) {
    puts("-1");
    return 0;
  }
  int k = 0;
  int c = 0;
  int t = n;
  while (n > 0) {
    if (k % 2 == 0) {
      for (int i = 0; i < b && n > 0; i++, c++) {
        if (i < a) {
          s[0][c] = t;
          t--;
          n--;
        } else {
          s[a][c] = 0;
        }
      }
    } else {
      for (int i = 0; i < b && n > 0; i++, c++) {
        if (i >= a) {
          s[0][c] = 0;
        } else {
          s[a][c] = t;
          t--;
          n--;
        }
      }
    }
    k++;
  }
  for (int i = 0; i < a; i++) {
    for (int j = 0; j < b; j++) {
      printf("%d ", s[i][j]);
    }
    printf("\n");
  }
}

#include <bits/stdc++.h>
