int n, m, k;
int a[1000010];
int main() {
  scanf("%d%d%d", &n, &m, &k);
  if (m * k < n) {
    printf("-1\n");
    return 0;
  }
  int c = n;
  int cur = 1, l = 1;
  while (c) {
    if (cur > m)
      for (int i = 0; i < k; i++) a[l++] = 0;
    else
      a[cur] = n - c + 1;
    c--;
    cur++;
    if (cur > m * k) cur = 1;
  }
  for (int i = 1; i <= m * k; i++) {
    if (i % k == 0)
      printf("\n");
    else
      printf(" ");
    printf("%d", a[i]);
  }
  return 0;
}

