using namespace std;
int main() {
  int n, a, b;
  cin >> n >> a >> b;
  vector<int> v1(a), v2(b);
  for (int i = 0; i < a; ++i) cin >> v1[i];
  for (int i = 0; i < b; ++i) cin >> v2[i];
  vector<int> v(n, 1), apos(a, 1), bpos(b, 1);
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < v1.size(); ++j)
      if (v1[j] == i + 1) apos[j] = 0;
    for (int j = 0; j < v2.size(); ++j)
      if (v2[j] == i + 1) bpos[j] = 0;
    if (apos[0] == 0 || bpos[0] == 0) v[i] = 2;
    for (int j = 0; j < apos.size(); ++j)
      if (apos[j] == 0) apos[j] = -1;
    for (int j = 0; j < bpos.size(); ++j)
      if (bpos[j] == 0) bpos[j] = -1;
    if (apos[0] == -1 && bpos[0] == -1) v[i] = 1;
  }
  for (int i = 0; i < n; ++i) cout << v[i] << " ";
  return 0;
}

