#include <bits/stdc++.h>
using namespace std;
char in[10000001];
char out[10000001];
int len;
bool check(char*);
int getNext(int);
int getNow(int);
int main() {
  scanf("%s", in);
  len = strlen(in);
  if (len == 1 && in[0] == '0') return printf("OK\n"), 0;
  scanf("%s", out);
  if (check(out)) return printf("OK\n"), 0;
  return printf("WRONG_ANSWER\n"), 0;
}
bool check(char* ans) {
  int last = 0;
  for (int i = 0; i < len; i++) {
    while (ans[last] > in[i]) last++;
    if (last > i) return false;
    if (ans[last] == in[i]) last++;
  }
  return last == len;
}
int getNext(int id) {
  int t = id, c = 0;
  while (t < len && in[t] == '0') {
    t++;
    c++;
  }
  if (t == len) t -= c;
  return t;
}
int getNow(int id) {
  int t = id;
  while (t < len && in[t]!= '0') t++;
  return t;
}

