#include <bits/stdc++.h>
using namespace std;
const long long MAXN = 2020;
const long long MAXP = 4e4 + 5;
long long dp[MAXN][MAXN];
long long t[MAXP];
int main() {
  ios::sync_with_stdio(0);
  cin.tie(0);
  long long N, M;
  cin >> N >> M;
  long long K;
  cin >> K;
  for (long long i = 1; i <= K; i++) {
    long long x, y;
    cin >> x >> y;
    t[i] = 1 + abs(x - N) + abs(y - M);
  }
  t[K + 1] = MAXN * MAXN;
  sort(t + 1, t + K + 1);
  long long L = 1 + abs(N - M);
  long long R = MAXN * MAXN;
  while (L <= R) {
    long long M = (L + R) / 2;
    for (long long i = 1; i <= K; i++)
      for (long long j = 1; j <= M; j++)
        dp[i][j] =
            min(dp[i - 1][j] + 1, dp[i][j - 1] + (t[i] == t[i - 1] + j));
    long long now = K + 1;
    for (long long i = 1; i <= K; i++)
      if (dp[i][M] + (t[i] == t[K] + M) <= M) now = min(now, i);
    if (now <= K) {
      L = M + 1;
    } else
      R = M - 1;
  }
  for (long long i = 1; i <= N; i++)
    for (long long j = 1; j <= M; j++)
      if (t[K] == t[K - 1] + abs(i - N) + abs(j - M))
        cout << i << " " << j << endl;
  return 0;
}

