#define ll long long
using namespace std;
const int N=2e5+10;
const int mod=998244353;
ll pow(ll a,ll b,ll mod)
{
	ll res=1;
	while(b)
	{
		if(b&1) res=res*a%mod;
		a=a*a%mod;
		b>>=1;
	}
	return res;
}
int fac[N],inv[N];
void init()
{
	fac[0]=1;
	for(int i=1;i<=N-1;i++) fac[i]=fac[i-1]*i%mod;
	inv[N-1]=pow(fac[N-1],mod-2,mod);
	for(int i=N-2;i>=0;i--) inv[i]=inv[i+1]*(i+1)%mod;
}
ll C(ll a,ll b)
{
	if(b<0) return 0;
	return fac[a]*inv[b]%mod*inv[a-b]%mod;
}
int main()
{
	init();
	int n,m;
	cin>>n>>m;
	vector<int> s(n),w(n);
	for(int i=0;i<n;i++) cin>>s[i];
	for(int i=0;i<n;i++) cin>>w[i];
	ll sum=0;
	for(int i=0;i<n;i++)
		if(s[i])
			sum=(sum+w[i])%mod;
	for(int i=0;i<n;i++)
	{
		if(!s[i]) continue;
		ll ans=(C((m+w[i]-1),m)-mod+sum)%mod;
		ans=(ans*inv[w[i]]+mod)%mod;
		cout<<ans<<endl;
	}
}

