#include <bits/stdc++.h>
using namespace std;
int const mod = 1e9 + 7;
long long fast_power(long long a, long long n) {
  long long ans = 1;
  while (n) {
    if (n & 1) ans = ans * a % mod;
    a = a * a % mod;
    n /= 2;
  }
  return ans;
}
long long solve(vector<int> &A, int n, int k) {
  long long ans = 0;
  for (int i = 0; i < (1 << n); i++) {
    long long mn = 1e9, mx = 0;
    for (int j = 0; j < n; j++) {
      if (i & (1 << j))
        mn = min(mn, A[j]);
      else
        mx = max(mx, A[j]);
    }
    if (k >= max(mx - mn, 0)) ans++;
  }
  return ans;
}
int main() {
  ios::sync_with_stdio(false);
  cin.tie(NULL);
  int n, k;
  cin >> n >> k;
  vector<int> A(n);
  for (int i = 0; i < n; i++) cin >> A[i];
  sort(A.begin(), A.end());
  cout << solve(A, n, k) % mod << '\n';
}

