#include <bits/stdc++.h>
using namespace std;
long long mod = 998244353;
long long modinv(long long a, long long m = mod) {
  long long m0 = m;
  long long t, q;
  long long x0 = 0, x1 = 1;
  if (m == 1) return 0;
  while (a > 1) {
    q = a / m;
    t = m;
    m = a % m, a = t;
    t = x0;
    x0 = x1 - q * x0;
    x1 = t;
  }
  if (x1 < 0) x1 += m0;
  return x1;
}
int main() {
  long long n, m, x;
  cin >> n >> m >> x;
  long long pw[m + 2];
  pw[0] = 1;
  for (long long i = 1; i <= m; i++) {
    pw[i] = 2 * pw[i - 1];
    if (pw[i] > n) {
      pw[i] = n;
    }
  }
  double ans = 0;
  for (long long i = 0; i < m; i++) {
    double p = x * (1.0 / 100.0);
    double d = pw[i] * (p * (1 + p * (p - 1) / 2) + 1);
    ans += modinv(pw[i + 1], n);
    ans *= d;
  }
  cout << fixed << setprecision(10) << ans << endl;
}

