#include <bits/stdc++.h>
using namespace std;
long long expo(long long a, long long n, long long mod) {
  long long ans = 1;
  while (n > 0) {
    if (n & 1) ans = (ans * a) % mod;
    a = (a * a) % mod;
    n >>= 1;
  }
  return ans % mod;
}
long long mod_inv(long long a) { return expo(a, MOD - 2, MOD); }
long long n, k, v[100], MOD;
bool is_prime(long long n) {
  if (n == 1 || n == 0) return false;
  long long temp = 2;
  while (temp * temp <= n) {
    if (n % temp == 0) return false;
    temp++;
  }
  return true;
}
int main() {
  cin >> n >> k;
  MOD = 1000000007;
  for (int i = 0; i < k; i++) {
    cin >> v[i];
  }
  long long ans = 1, cur = 0, pw = 1;
  bool is_prime_n = false;
  for (int i = 0; i < k; i++) {
    long long t = n / v[i];
    if (t >= MOD) {
      if (!is_prime_n) {
        cur += mod_inv(expo(v[i], MOD - 2, MOD) - 1);
        if (is_prime(v[i])) {
          cur = cur * (v[i] - 1) % MOD;
        }
      } else {
        cur +=
            ((((n / v[i]) % MOD) * mod_inv(v[i] - 1)) % MOD + MOD) % MOD;
      }
    } else if (t > 0) {
      cur += mod_inv(expo(v[i], t, MOD) - 1);
      is_prime_n = true;
    }
    if (!is_prime(v[i])) {
      ans = ans * ((mod_inv(v[i]) - 1 + MOD) % MOD) % MOD;
    }
  }
  if (!is_prime_n) {
    ans = ans * cur % MOD;
  }
  cout << ans << endl;
}

