  int flag = 0, flag1 = 0, t = 0, m = 0;
  while (true) {
    if (!x &&!y) break;
    if (!x)
      x = x + y, y = 0;
    else if (!y)
      y = y - x, x = 0;
    else if (x && y) {
      if (x == y &&!flag) {
        x = 0, y = 0, flag = 1;
      } else if (x < y &&!flag1) {
        swap(x, y), flag1 = 1;
      } else if (!flag) {
        x = x - y, y = 0, flag = 1;
      } else if (x + y == 0 && flag1) {
        x = 0, y = 0, flag = 1, flag1 = 1;
      } else
        break;
    }
    x -= c, y -= d;
    t++, m++;
  }
  if (!x &&!y)
    printf("YES\n");
  else
    printf("NO\n");
}

