#include <bits/stdc++.h>
using namespace std;
const int N = 1000007, M = 1000000007;
long long fact[N + 1], inv[N + 1];
int n, m;
long long pow(long long a, int b) {
  long long ans = 1;
  for (; b; b /= 2, a = a * a % M)
    if (b & 1) ans = ans * a % M;
  return ans;
}
long long comb(int a, int b) {
  return (fact[a] * inv[b] % M) * inv[a - b] % M;
}
int main() {
  cin >> n;
  fact[0] = 1;
  for (int i = 1; i <= n; i++) {
    fact[i] = fact[i - 1] * (i + 1) % M;
  }
  inv[n] = pow(fact[n], M - 2);
  for (int i = n - 1; ~i; i--) inv[i] = inv[i + 1] * (i + 1) % M;
  m = min(n, n / 2 + 1);
  long long ans = 0;
  for (int i = 1; i <= m; i++) {
    ans += comb(n - i + 2, 2);
  }
  cout << ans % M << endl;
  return 0;
}

