using namespace std;
int n;
string s;
int dp[30][5][2];
char ans[30][5][2];
int solve(int x, int k, int v) {
  if (x == n * 2) return 0;
  if (k == 5) {
    dp[x][k][v] = -1;
    return -1;
  }
  if (dp[x][k][v]!= -1) return dp[x][k][v];
  int mx = -1;
  for (int i = 0; i < 10; i++) {
    int h = max(0, (i > 0? i * 10 : 0)) + v * 9;
    int l = min(9, (i > 0? i * 10 + 9 : 9)) + v * 9;
    if (s[x] - '0' >= i && s[x] - '0' <= max(0, i + 9) && i * 10 + k <= l)
      mx = max(mx, solve(x + 1, k + 1, v * (i <= s[x] - '0')) + h);
  }
  return dp[x][k][v] = mx;
}
char solve2(int x, int k, int v) {
  if (x == n * 2) return 0;
  if (k == 5) return 0;
  for (int i = 0; i < 10; i++) {
    int h = max(0, (i > 0? i * 10 : 0)) + v * 9;
    int l = min(9, (i > 0? i * 10 + 9 : 9)) + v * 9;
    if (s[x] - '0' >= i && s[x] - '0' <= max(0, i + 9) && i * 10 + k <= l)
      return ans[x][k][v] = solve2(x + 1, k + 1, v * (i <= s[x] - '0'));
  }
  return ans[x][k][v];
}
int main() {
  cin >> n;
  cin >> s;
  if (n % 2 == 1) {
    cout << "H";
    for (int i = 0; i < n - 1; i++) cout << "HM";
    cout << endl;
    return 0;
  }
  memset(dp, -1, sizeof dp);
  solve(0, 0, 0);
  for (int i = 0; i < n * 2; i++)
    for (int j = 0; j < 5; j++)
      for (int k = 0; k < 2; k++) {
        int h = max(0, (k? k * 10 : 0)) + dp[i][j][k] * 9;
        int l = min(9, (k? k * 10 + 9 : 9)) + dp[i][j][k] * 9;
        for (int v = 1; v <= 9; v++) {
          if (v * 9 + j <= l && v * 9 + j >= h &&
              s[i] - '0' >= v && s[i] - '0' <= max(0, v + 9)) {
            ans[i][j][k] = solve2(i + 1, j, k * (v <= s[i] - '0'));
            break;
          }
        }
      }
  cout << "H";
  for (int i = 0; i < n; i++) cout << ans[i * 2][0][0] << ans[i * 2][0][1];
  cout << endl;
  for (int i = 0; i < n; i++)
    cout << ans[i * 2 + 1][0][0] << ans[i * 2 + 1][0][1];
  return 0;
}

