#include <bits/stdc++.h>
using namespace std;
double posx, posy, posz, vx, vy, vz, px, py, pz, vp, vs, d;
int n;
void calc(double t, double x, double y, double z, double vx, double vy,
          double vz) {
  d = sqrt((x - px) * (x - px) + (y - py) * (y - py) + (z - pz) * (z - pz));
  if (d < 1e-8) return;
  if ((px - x) * vx + (py - y) * vy + (pz - z) * vz < 0) return;
  if ((px - x) * vx + (py - y) * vy + (pz - z) * vz < vs * t &&
      (px - x) * vx + (py - y) * vy + (pz - z) * vz >= 0.0 &&
      (px - x) * vx + (py - y) * vy + (pz - z) * vz < 1e-10) {
    printf("YES\n%.8lf\n", t);
    printf("%.8lf %.8lf %.8lf", x, y, z);
    exit(0);
  }
}
int main() {
  cin >> n;
  double x, y, z;
  cin >> x >> y >> z;
  for (int i = 1; i <= n; i++) {
    cin >> posx >> posy >> posz;
    calc(i - 1.0, x, y, z, vx, vy, vz);
    x = posx;
    y = posy;
    z = posz;
  }
  cin >> vx >> vy >> vz >> px >> py >> pz >> vp >> vs;
  vx /= vp;
  vy /= vp;
  vz /= vp;
  calc(n, x, y, z, vx, vy, vz);
  printf("NO\n");
  return 0;
}

