bool dfs(int u) {
  vis[u] = 1;
  for (int i = 0; i < g[u].size(); i++) {
    int v = g[u][i];
    if (!vis[v]) {
      if (dfs(v)) return true;
    }
  }
  if (t[u] == 0) {
    if (!vis[t[u]]) {
      if (dfs(t[u])) return true;
    }
  } else {
    for (int i = 0; i < t[u]; i++) {
      int v = g[u][i];
      if (t[v] > 0 &&!vis[v]) {
        if (dfs(v)) return true;
      }
    }
  }
  return false;
}
