int check(node A, node B, node C, node D) {
  int c1, c2;
  int m1, m2;
  c1 = (B.x - A.x) * (D.y - A.y) - (B.y - A.y) * (D.x - A.x);
  c2 = (C.x - A.x) * (D.y - A.y) - (C.y - A.y) * (D.x - A.x);
  if ((c1 > 0 && c2 < 0) || (c1 < 0 && c2 > 0))
    return 1;
  else
    return 0;
}
int main() {
