#include <bits/stdc++.h>
const double INF = 1e14;
struct Point {
  double x, y;
  Point operator-(const Point& b) const {
    Point ans;
    ans.x = x - b.x;
    ans.y = y - b.y;
    return ans;
  }
  double cross(const Point& b) const { return x * b.y - y * b.x; }
  double dis(const Point& b) const {
    return sqrt((x - b.x) * (x - b.x) + (y - b.y) * (y - b.y));
  }
};
int main() {
  int t;
  scanf("%d", &t);
  while (t--) {
    int a, b;
    scanf("%d%d%d", &a, &b, &b);
    Point p;
    p.x = a;
    p.y = b;
    Point s[5];
    s[0] = (Point){INF, -INF};
    s[1] = (Point){-INF, -INF};
    s[2] = (Point){INF, INF};
    s[3] = (Point){-INF, INF};
    s[4] = p;
    for (int i = 0; i < 4; i++) {
      for (int j = 1; j <= 3; j++)
        if (s[i] > s[j]) std::swap(s[j], s[i]);
    }
    int tag = 0;
    for (int i = 4; i <= 3 + t; i++) {
      tag += ((s[1] - p) * (s[2] - p) <= 0 && (s[2] - p) * (s[3] - p) <= 0 &&
              (s[3] - p) * (s[1] - p) <= 0);
    }
