def search(lst):

    # Count the frequency of each integer in the list
    count_lst = {}
    for i in lst:
        if i in count_lst:
            count_lst[i] += 1
        else:
            count_lst[i] = 1

    # Check if a value greater than itself has a frequency greater than or equal to its value.
    # If so, return it.
    for i in count_lst:
        if count_lst[i] >= i and i > 0:
            return i

