def max_fill(grid, capacity):
    import math
    ROW, COL = len(grid), len(grid[0])
    # Fill each column using bucket capacity.
    for col in range(COL):
        capacity_filled = 0
        # Find the maximum number of row index.
        max_row = 0
        for row in range(ROW):
            if grid[row][col] == 1:
                max_row = max(max_row, row)

        # Fill the column using the max row index.
        while max_row >= 0 and capacity_filled < capacity:
            if grid[max_row][col] == 1:
                grid[max_row][col] = 0
                capacity_filled += 1
            max_row -= 1

    # Return the total number of rows having a 1.
    count = 0
    for row in grid:
        for cell in row:
            if cell == 1:
                count += 1
    return count
