def match_parens(lst):
    """
    Given a list of two strings, returns 'Yes' if it is possible to concatenate
    the two strings in such a way that the result will be good.
    Otherwise, returns 'No'.
    """
    # A string is 'good' if and only if its parentheses are balanced.
    def good(string):
        count = 0
        for char in string:
            if char == '(':
                count += 1
            elif char == ')':
                count -= 1
            if count < 0:
                return False
        if count == 0:
            return True
        else:
            return False

    # Concatenate the two strings.
    # If concatenating either of the two strings results in a string that is
    # not good, return 'No'.
    if lst[0] == "(" and lst[1] == ")" or lst[1] == "(" and lst[0] == ")" or good(lst[0] + lst[1]) == True:
        return 'Yes'
    else:
        return 'No'
