def get_odd_collatz(n):
    if n == 1:
        return [1]
    sequence = []
    while n!= 1:
        if n % 2 == 0:
            n = n // 2
        else:
            n = 3 * n + 1
        sequence.append(n)
    return sorted([i for i in sequence if i % 2!= 0])

