def getNeighborCells(grid: List[List[int]]) -> List[List[int]]:
    n = len(grid)
    neighbor = []
    for i in range(n):
        for j in range(n):
            if i - 1 >= 0:
                neighbor.append([i - 1, j])
            if i + 1 < n:
                neighbor.append([i + 1, j])
            if j - 1 >= 0:
